import { useEffect } from 'react';
import { useSelector } from 'react-redux';
import axios from '../../api';
import { useParams } from 'react-router-dom';

export default function Profile({ tweet }) {
  const { id } = useParams();
  const { token } = useSelector((state) => state.user);

  useEffect(() => {
    (async () => {
        try  {
       const response = await axios.get(`/twitter/profile/${id}`,  {
          headers: {
            'Content-Type': 'application/json',
            Authorization: 'Bearer ' + token,
          },
        });
        console.log(`✅ ${response.status} ${response.statusText}`);
      } catch (error) {
        console.error('❌', error);
      }
          
    })();
  }, []);

  const componentStyles = {
      flex: '1',
      border: '0.5px solid #f5f5f5',
    },
    titleStyles = {
      padding: '0.7em',
      fontSize: '1.2em',
      fontWeight: '900',
    };

    return (
        <div className={styles.box}>
          <ProfilePicture image={tweet.from.profilePicture} />
    
          <div>
            <div className={styles.head} onClick={() => history.push('/profile/' + tweet.from._id)}>
              <span>{tweet.from.name}</span> @{tweet.from.username} • {timeAgo(tweet.createdAt)}
            </div>
    
            <div className={styles.tweet} onClick={() => history.push('/tweet/' + tweet._id)}>
              {tweet.message}
              {tweet.image && (
                <img
                  src={tweet.image}
                  alt='tweet_image'
                  style={{ borderRadius: '10px', margin: '10px 0 0 0', width: '100%' }}
                />
              )}
            </div>
    
            <div className={styles.tools}>
              <div className={styles.comment}>
                <CommentIcon />
                {tweet.comments.length}
              </div>
              <div className={styles.retweet}>
                <ReTweetIcon />
                {tweet.retweets.length}
              </div>
              <div className={styles.like}>
                <HeartIcon />
                {tweet.likes.length}
              </div>
            </div>
          </div>
        </div>
      );
}
